import time
import re
from tqdm import tqdm
import numpy as np


def minion_game(string):
    # Defines words and scores, vowels, and dictionary to keep counter
    kevin_words = set()
    stuart_words = set()
    kevin_score = 0
    stuart_score = 0
    vowels = 'AEIOU'
    substring_count = {}  # may be unnecessary

    # Iterates over string and finds all possible break-ups of the word and how many times they appear in the original string, including overlapping
    for i in tqdm(range(len(string))):
        step_string = string[i:]
        is_vowel = step_string[0] in vowels
        for j in range(i, len(string)):
            substring = string[i:j + 1]
            substring2 = step_string[:j+1]

            # Create dictionary of substrings and counts
            # substring_count[substring] = sum(string[i:].startswith(substring) for i in range(len(string)))

            # Assign to kevin or stuart
            if substring[0] in vowels:
                if substring not in kevin_words:
                    kevin_words.add(substring)
                    kevin_score += sum(string[i:].startswith(substring) for i in range(len(string)))
            elif substring not in stuart_words:
                stuart_words.add(substring)
                stuart_score += sum(string[i:].startswith(substring) for i in range(len(string)))

    # Define winner and print
    winner = 'Stuart' if stuart_score > kevin_score else 'Kevin' if kevin_score > stuart_score else 'Draw'
    if winner == 'Draw':
        print(winner)
    else:
        print(winner + " " + str(max(stuart_score, kevin_score)))


