def minion_game_v3(string):
    # Defines words and scores, vowels, and dictionary to keep counter
    vowels = 'AEIOU'

    # Count number of vowels and consonants then multiple by len(string) - position of character
    kevin_score = sum([len(string) - i for i, char in enumerate(string) if char in vowels])
    stuart_score = sum([len(string) - i for i, char in enumerate(string) if char not in vowels])

    # Define winner and print
    winner = 'Stuart' if stuart_score > kevin_score else 'Kevin' if kevin_score > stuart_score else 'Draw'
    if winner == 'Draw':
        print(winner)
    else:
        print(winner + " " + str(max(stuart_score, kevin_score)))


def minion_game_v4(string):
    # Defines words and scores, vowels, and dictionary to keep counter
    vowels = 'AEIOU'

    string_len = len(string)
    # Count number of vowels and consonants then multiple by len(string) - position of character
    kevin_score = sum([string_len - i for i, char in enumerate(string) if char in vowels])
    stuart_score = ((string_len * (string_len-1)) / 2) - kevin_score

    # Define winner and print
    winner = 'Stuart' if stuart_score > kevin_score else 'Kevin' if kevin_score > stuart_score else 'Draw'
    if winner == 'Draw':
        print(winner)
    else:
        print(winner + " " + str(max(stuart_score, kevin_score)))


def minion_game_numpy(string):
    # Defines words and scores, vowels, and dictionary to keep counter
    vowels = 'AEIOU'

    # Count number of vowels and consonants then multiple by len(string) - position of character
    positions = np.array([i for i, char in enumerate(string) if char in vowels])
    kevin_score = sum(len(string) - positions)

    positions = np.array([i for i, char in enumerate(string) if char not in vowels])
    stuart_score = sum(len(string) - positions)

    # Define winner and print
    winner = 'Stuart' if stuart_score > kevin_score else 'Kevin' if kevin_score > stuart_score else 'Draw'
    if winner == 'Draw':
        print(winner)
    else:
        print(winner + " " + str(max(stuart_score, kevin_score)))


